DROP TABLE IF EXISTS `students`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `students` (
  `id` char(14) NOT NULL COMMENT '学号(格式：年份4位+部门号4位+专业号2位+班级号2位+学生编号2位)',
  `name` varchar(50) NOT NULL COMMENT '姓名',
  `gender` enum('男','女') NOT NULL COMMENT '性别',
  `id_card` varchar(18) DEFAULT NULL COMMENT '身份证号',
  `birthday` date DEFAULT NULL COMMENT '出生日期',
  `ethnicity` varchar(20) DEFAULT NULL COMMENT '民族',
  `political_status` varchar(20) DEFAULT NULL COMMENT '政治面貌',
  `class_id` varchar(20) NOT NULL COMMENT '所属班级',
  `major_id` varchar(20) NOT NULL COMMENT '所属专业',
  `department_id` varchar(20) NOT NULL COMMENT '所属院系',
  `education_level` varchar(20) NOT NULL COMMENT '培养层次',
  `enrollment_date` date NOT NULL COMMENT '入学日期',
  `expected_graduation` date DEFAULT NULL COMMENT '预计毕业日期',
  `student_status` varchar(20) DEFAULT '在读' COMMENT '学籍状态',
  `is_active` tinyint(1) DEFAULT '1' COMMENT '是否在校',
  `phone` varchar(20) DEFAULT NULL COMMENT '联系电话',
  `email` varchar(50) DEFAULT NULL COMMENT '电子邮箱',
  `address` varchar(200) DEFAULT NULL COMMENT '家庭住址',
  `notes` text COMMENT '备注',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `idx_class` (`class_id`),
  KEY `idx_major` (`major_id`),
  KEY `idx_department` (`department_id`),
  KEY `idx_student_status` (`student_status`),
  KEY `idx_class_status` (`class_id`,`student_status`),
  CONSTRAINT `students_ibfk_2` FOREIGN KEY (`major_id`) REFERENCES `majors` (`id`),
  CONSTRAINT `students_ibfk_3` FOREIGN KEY (`department_id`) REFERENCES `departments` (`id`),
  CONSTRAINT `check_student_id_format` CHECK (regexp_like(`id`,_utf8mb4'^[0-9]{14}$'))
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='学生表';
