/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `before_student_insert` BEFORE INSERT ON `students` FOR EACH ROW BEGIN
    -- 检查学号格式是否正确（14位数字）
    IF NEW.id NOT REGEXP '^[0-9]{14}$' THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = '学号必须是14位数字';
    END IF;

    -- 检查年份部分（前4位）是否是有效的年份（假设从2000年开始）
    IF CAST(SUBSTRING(NEW.id, 1, 4) AS UNSIGNED) < 2000 OR
       CAST(SUBSTRING(NEW.id, 1, 4) AS UNSIGNED) > YEAR(CURDATE()) THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = '学号的年份部分无效（必须在2000年到当前年份之间）';
    END IF;
END */;;
