CREATE TABLE `teachers` (
  `id` char(11) NOT NULL COMMENT '教师工号(格式：入职年份4位+部门号4位+序列号3位)',
  `name` varchar(50) NOT NULL COMMENT '姓名',
  `english_name` varchar(50) DEFAULT NULL COMMENT '英文名字',
  `gender` enum('男','女') NOT NULL COMMENT '性别',
