  `satisfaction` tinyint(1) DEFAULT '1' COMMENT '教室位置满意度',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `phone` varchar(20) DEFAULT NULL COMMENT '联系电话',
  `email` varchar(100) DEFAULT NULL COMMENT '电子邮箱',
  `max_weekly_hours` int DEFAULT '16' COMMENT '最大周课时',
  `research_direction` varchar(200) DEFAULT NULL COMMENT '研究方向',
  `is_active` tinyint(1) DEFAULT '1' COMMENT '是否激活（0-禁用，1-启用）',
  `last_login` datetime(6) DEFAULT NULL,
  `password_hash` varchar(60) DEFAULT NULL,
  `password_updated_at` datetime(6) DEFAULT NULL,
