/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `before_teacher_insert` BEFORE INSERT ON `teachers` FOR EACH ROW BEGIN
    -- 检查工号格式是否正确（11位数字）
    IF NEW.id NOT REGEXP '^[0-9]{11}$' THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = '教师工号必须是11位数字';
    END IF;

    -- 检查年份部分（前4位）是否是有效的年份（假设从2000年开始）
    IF CAST(SUBSTRING(NEW.id, 1, 4) AS UNSIGNED) < 2000 OR
       CAST(SUBSTRING(NEW.id, 1, 4) AS UNSIGNED) > YEAR(CURDATE()) THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = '教师工号的年份部分无效（必须在2000年到当前年份之间）';
    END IF;

    -- 检查部门号（中间4位）是否存在
    IF NOT EXISTS (SELECT 1 FROM departments WHERE id = SUBSTRING(NEW.id, 5, 4)) THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = '教师工号中的部门号不存在';
    END IF;

    -- 检查序列号（最后3位）在同一部门同一年份中是否唯一
    IF EXISTS (
        SELECT 1
        FROM teachers
        WHERE SUBSTRING(id, 1, 8) = SUBSTRING(NEW.id, 1, 8)  -- 相同年份和部门
        AND SUBSTRING(id, 9, 3) = SUBSTRING(NEW.id, 9, 3)    -- 相同序号
        AND id != NEW.id  -- 排除自身
    ) THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = '该部门该年份中已存在相同的教师序号';
    END IF;
END */;;
